(function ( $ ) {
   
$.fn.readingList = function(options) {
  var $container = $(this);
  console.log($container);
  var settings = $.extend({
    limit: 5,
    filter: "favorites"
  }, options );
  console.log(settings.limit);
  
  url = 'http://proxy.readinglistjs.jshawl.com/?access_token=69391704-b853-ba68-f903-c0e281';
  $.ajax({
    type:'GET',
    url:url,
    dataType :"jsonp",
    jsonp: false,
    jsonpCallback: "myJsonMethod",
    success: function(res){
      $.each(res.list, function(index, obj){
        $container.append($('<div><h1><a href="'+obj.resolved_url+'" target="_blank">'+obj.resolved_title+'</a></h1><p>'+obj.excerpt+'</p></div>'))
      })
    }
  });
  return this;
};

}( jQuery ));
